applyingMountingStyle.swift:
struct MyWidget: Widget {
    var body: some WidgetConfiguration {
        StaticConfiguration(...) { entry in
            MyWidgetView(entry: entry)
        }
        .supportedMountingStyles([.elevated, .recessed])
    }
}

applyingTexture.swift:
struct MyWidgetView: View {
    var body: some View {
        VStack {
            // Widget content
        }
        .widgetTexture(.glass)
    }
}

respondingToLevelOfDetail.swift:
@Environment(\.widgetLevelOfDetail) var levelOfDetail

var body: some View {
    if levelOfDetail == .simplified {
        SimplifiedWidgetContent()
    } else {
        DetailedWidgetContent()
    }
}
